package JavaBasic.ControlStateMent;

public class elseif {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
             int a=10;
             int b=20;
             int c=30;
             if(a>b)
             {
            	 System.out.println("if statment checking next");
             }
             else if(b>c)
             {
            	 System.out.println("else if check next loop");
             }
             else if(c>a)
             {
            	 System.out.println("Check Final Loop");
             }
             else
             {
            	 System.out.println("Exit");
             }
	}

}
